mapper.select:
SELECT IFNULL(
(select pre_status from personnel where pre_phone = #{prePhone}
and pre_id !=  #{preId} limit 1),2)

SELECT IFNULL(
 ( select pre_status from personnel where pre_email = #{preEmail}
 and pre_id !=  #{preId} limit 1),2)

mapper.insert:
update personnel
pre_name=#{preName}, pre_phone=#{prePhone}, pre_email=#{preEmail}, pre_password=#{prePassword}, pre_gender=#{preGender}, pre_role_id=#{preRoleId}, pre_status=#{preStatus}, pre_updaDate = NOW()
where pre_id = #{preId}
