mapper.select:
SELECT tem_id teamId,tem_name teamName,pre_name leaderName,COUNT(fly_tem_id) recordCount FROM team LEFT JOIN personnel ON tem_lead=pre_id LEFT JOIN family ON fly_tem_id=tem_id AND fly_status=1
AND fly_date >= #{start}
AND fly_date <= #{end}
GROUP BY tem_id

SELECT tem_id teamId,tem_name teamName,pre_name leaderName,COUNT(purse_payDate) payCount FROM team LEFT JOIN family ON fly_tem_id=tem_id LEFT JOIN personnel ON tem_lead=pre_id LEFT JOIN purchase ON purse_fly_id=fly_id AND purse_status=1
AND purse_payDate>=#{start}
AND purse_payDate<=#{end}
GROUP BY tem_id

SELECT tem_id teamId,tem_name teamName,pre_name leaderName,SUM(tem_lib_num) outCount FROM team LEFT JOIN personnel ON tem_lead=pre_id LEFT JOIN member ON mem_tem_id=tem_id LEFT JOIN team_liblog ON tem_lib_operator=mem_mem AND tem_lib_type=1
AND tem_lib_creaDate>=#{start}
AND tem_lib_creaDate<=#{end}
GROUP BY tem_id

SELECT tem_id teamId,tem_name teamName,pre_name leaderName,SUM(or_ept_num) installCount FROM team LEFT JOIN personnel ON tem_lead=pre_id LEFT JOIN family ON tem_id=fly_tem_id LEFT JOIN purchase ON purse_fly_id=fly_id LEFT JOIN `order` ON or_purse_id=purse_id LEFT JOIN `install` ON ill_purse_id=purse_id AND ill_status=1
AND ill_installDate>=#{start}
AND ill_installDate<=#{end}
GROUP BY tem_id
